sys_transform_map.script:
(function transformRow(source, target, map, log, isUpdate) {

//set State and Substate

	target.install_status = 1;
	if (source.u_hardware_state == 'INSTALLED/ACTIVE'){
		target.install_status = 1;
	}	
	if (source.u_hardware_state == 'PENDING DISPOSAL'){
		target.install_status = 6;
		target.substatus = 'pending_disposal';
	}
	if (source.u_hardware_state == 'AVAILABLE/STORAGE'){
		target.install_status =6;
		target.substatus = 'available';
	}
	if (source.u_hardware_state == 'RECEIVED'){
		target.install_status =6;
		target.substatus = 'reserved';	
		}


})(source, target, map, log, action==="update");
